import React from 'react';
import './about.css';
import { Col, Row } from 'reactstrap';
import kenny from '../assets/ken.png';
import pattern from '../assets/path25.png';
import Fade from 'react-reveal/Fade';
import Zoom from 'react-reveal/Zoom';

const About = () => {
	return (
		<div className="about" id="about">
			<Row className="columns">
				<Col className="prez" md="9">
					<Fade left collapse>
						<h1 className="presentation">PRESENTATION</h1>
					</Fade>
					<Fade left>
						<h2 className="name">KENNY PHIRI - Développeur</h2>
					</Fade>
					<p className="me">
						Développeur web fullstack autodidacte de 28 ans basé sur Avignon, j'ai suivi une formation de
						développeur web à la Wild Code School. 
						Le développement web est une de mes passions que je cultive au quotidien. 
						Je suis passionné par l'UX-UI design, les créations web, la manipulation de bases de données et
						toutes les dernières technologies. 
						J'aime expérimenter, créer et relever de nouveaux défis. 
						Si vous êtes intéressé par mon profil ou que vous voulez en savoir plus sur moi, n'hésitez pas à
						me contacter.
					</p>
				</Col>
				<Col className="image" md="3">
					<Zoom left fraction={1}>
						<img className="pattern" src={pattern} alt="" />
						<img className="kennys" src={kenny} alt="" />
					</Zoom>
				</Col>
			</Row>
		</div>
	);
};

export default About;
